function formatResult(result, container, query, escapeMarkup) {
    var markup=[];
    window.Select2.util.markMatch(result.text, query.term, markup, escapeMarkup);
    if (result.isNew) {
        return markup.join("");
    } else {
        return '<span class="post-tag">'+markup.join("")+'</span><span class="item-multiplier">Ã—&nbsp;'+result.count+'</span>';
    }
};
